def main():
    student_grades = {}  # Initialize the dictionary that will store the student names as keys and grades as values

    while True:
        # Display the menu
        print("\n1. Add Student Grade")
        print("2. Get Student Grade")
        print("3. Display All Grades")
        print("4. Exit")
        choice = input("Enter an option: ")

        if choice == '1':
            # Option to add a student's grade
            name = input("Enter student's name: ")
            grade = input("Enter student's grade: ")
            student_grades[name] = grade
            print("Grade added.")

        elif choice == '2':
            # Option to get a student's grade
            name = input("Enter student's name to retrieve grade: ")
            grade = student_grades.get(name, None)
            if grade:
                print(f"{name}'s grade is {grade}")
            else:
                print("Student not found.")

        elif choice == '3':
            # Option to display all student grades
            if student_grades:
                print("\nAll Student Grades:")
                for name, grade in student_grades.items():
                    print(f"{name}: {grade}")
            else:
                print("No students' grades to display.")

